(()=>{const e=document.querySelector("button.search-btn"),t=document.querySelector("input.search-box-input"),n=document.querySelector(".weather-description"),c=document.querySelector(".weather-location"),o=document.querySelector(".temperature");e.addEventListener("click",(()=>async function(e){const t=await fetch(`https://api.weatherapi.com/v1/current.json?key=f12e63291c54404c94063710241102&q=${e}`,{mode:"cors"}),r=await t.json();console.log(r),c.textContent=r.location.name,n.textContent=`The weather is ${r.current.condition.text}`,o.textContent=`${r.current.temp_c}°C/ ${r.current.temp_f}°F`}(t.value)))})();